DROP DATABASE IF EXISTS `Test_2024_05`;
CREATE DATABASE `Test_2024_05`;
USE `Test_2024_05`;

CREATE TABLE FAQ (
	id INT(10) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
	`regDate`	DATETIME NOT NULL COMMENT '작성날짜',
	`updateDate`	DATETIME NOT NULL COMMENT '수정날짜',
	`question`	CHAR(100)	NULL	COMMENT '질문',
	`answer`	TEXT	NOT NULL	COMMENT '답변'
);


CREATE TABLE `TouristDestination` (
	`id` INT(10) UNSIGNED NOT NULL PRIMARY KEY AUTO_INCREMENT,
	`division` TEXT ,
	`placename` TEXT ,
	`location` TEXT ,
	`phoneNumber` TEXT ,
	`URL` TEXT ,
	`department` TEXT ,
	`departmentphoneNumber` TEXT 
);

